INSERT INTO `wp_postmeta` VALUES ('625', '267', 'wp-syntax-cache-content', 'a:1:{i:1;s:2397:\"\n<div class=\"wp_syntax\" style=\"position:relative;\"><table><tr><td class=\"code\"><pre class=\"bash\" style=\"font-family:monospace;\"><span style=\"color: #666666; font-style: italic;\">#/bin/bash</span>\n<span style=\"color: #000000; font-weight: bold;\">for</span> i <span style=\"color: #000000; font-weight: bold;\">in</span> <span style=\"color: #000000; font-weight: bold;\">`</span><span style=\"color: #c20cb9; font-weight: bold;\">cat</span> http.txt<span style=\"color: #000000; font-weight: bold;\">`</span>;<span style=\"color: #000000; font-weight: bold;\">do</span>\n  <span style=\"color: #c20cb9; font-weight: bold;\">dig</span> <span style=\"color: #000000; font-weight: bold;\">@</span>114.114.114.114 <span style=\"color: #007800;\">$i</span> <span style=\"color: #000000; font-weight: bold;\">|</span><span style=\"color: #c20cb9; font-weight: bold;\">awk</span> <span style=\"color: #ff0000;\">\'/cn|com/\'</span><span style=\"color: #000000; font-weight: bold;\">|</span><span style=\"color: #c20cb9; font-weight: bold;\">awk</span>  <span style=\"color: #ff0000;\">\'{print$5}\'</span> <span style=\"color: #000000; font-weight: bold;\">|</span><span style=\"color: #c20cb9; font-weight: bold;\">grep</span> <span style=\"color: #000000;\">1</span> <span style=\"color: #000000; font-weight: bold;\">&gt;&gt;</span> http_filter.txt\n  <span style=\"color: #c20cb9; font-weight: bold;\">dig</span> <span style=\"color: #000000; font-weight: bold;\">@</span>8.8.8.8 <span style=\"color: #007800;\">$i</span> <span style=\"color: #000000; font-weight: bold;\">|</span><span style=\"color: #c20cb9; font-weight: bold;\">awk</span> <span style=\"color: #ff0000;\">\'/cn|com/\'</span><span style=\"color: #000000; font-weight: bold;\">|</span><span style=\"color: #c20cb9; font-weight: bold;\">awk</span>  <span style=\"color: #ff0000;\">\'{print$5}\'</span> <span style=\"color: #000000; font-weight: bold;\">|</span><span style=\"color: #c20cb9; font-weight: bold;\">grep</span> <span style=\"color: #000000;\">1</span> <span style=\"color: #000000; font-weight: bold;\">&gt;&gt;</span> http_filter.txt\n<span style=\"color: #000000; font-weight: bold;\">done</span></pre></td></tr></table><p class=\"theCode\" style=\"display:none;\">#/bin/bash\r\nfor i in `cat http.txt`;do\r\n  dig @114.114.114.114 $i |awk \'/cn|com/\'|awk  \'{print$5}\' |grep 1 &gt;&gt; http_filter.txt\r\n  dig @8.8.8.8 $i |awk \'/cn|com/\'|awk  \'{print$5}\' |grep 1 &gt;&gt; http_filter.txt\r\ndone</p></div>\n\";}');
