INSERT INTO `wp_posts` VALUES ('55', '1', '2017-10-30 23:21:47', '2017-10-30 15:21:47', '<pre lang=\"bash\" line=\"1\">\r\n#!/bin/bash\r\nDate=`date +%Y%m%d%H%M`\r\nFILE=/home/apnic.txt\r\nmkdir /home/ip$Date\r\nDIR=\"/home/ip$Date\"\r\nrm -f $FILE\r\nwget http://ftp.apnic.net/apnic/stats/apnic/delegated-apnic-latest -O $FILE\r\nfunction compute_mask()\r\n{\r\nlocal array_ip=($(awk -F \"|\" \'{print $1}\' $1))\r\nlocal array_mask=($(awk -F \"|\" \'{print $2}\' $1))\r\nfor ((i=0,j=0;i<${#array_ip[@]},j<${#array_mask[@]};i++,j++));\r\n  do\r\n    ev_ip=${array_ip[$i]}\r\n    ev_mask=${array_mask[j]}\r\n      for ((a=1;a<=32;a++));\r\n        do\r\n          if [ `echo $[2**$a]` -eq $ev_mask ];then\r\n            ar_mask=`expr 32 - $a`\r\n            echo \"$ev_ip/$ar_mask\"\r\n          fi\r\n        done\r\n  done\r\n}\r\n#China routing table\r\ngrep \"CN|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' >$DIR/cnip.txt\r\ncompute_mask $DIR/cnip.txt >>cnip.txt\r\n#Hong Kong routing table\r\ngrep \"HK|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' >$DIR/hkip.txt\r\ncompute_mask $DIR/hkip.txt >>hkip.txt\r\n#Australia\'s routing table\r\ngrep \"AU|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' >$DIR/auip.txt\r\ncompute_mask $DIR/auip.txt >>auip.txt\r\n#South Korea\'s routing table\r\ngrep \"KR|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' >$DIR/krip.txt\r\ncompute_mask $DIR/krip.txt >>krip.txt\r\n#Tai Wan routing table\r\ngrep \"TW|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' >$DIR/twip.txt\r\ncompute_mask $DIR/twip.txt >>twip.txt\r\n#Singapore routing table\r\ngrep \"SG|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' >$DIR/sgip.txt\r\ncompute_mask $DIR/sgip.txt >>sgip.txt\r\n#Japan\'s routing table\r\ngrep \"JP|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' >$DIR/jpip.txt\r\ncompute_mask $DIR/jpip.txt >>jpip.txt\r\n</pre>', '通过shell在APNIC上获取IP地址路由表', '', 'publish', 'open', 'open', '', '%e9%80%9a%e8%bf%87shell%e5%9c%a8apnic%e4%b8%8a%e8%8e%b7%e5%8f%96ip%e5%9c%b0%e5%9d%80%e8%b7%af%e7%94%b1%e8%a1%a8', '', '', '2017-10-31 02:03:06', '2017-10-30 18:03:06', '', '0', 'https://blog.csking.cn/?p=55', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('57', '1', '2017-10-30 23:20:11', '2017-10-30 15:20:11', '#!/bin/bash\r\nDate=`date +%Y%m%d%H%M`\r\nFILE=/home/apnic.txt\r\nmkdir /home/ip$Date\r\nDIR=\"/home/ip$Date\"\r\nrm -f $FILE\r\nwget http://ftp.apnic.net/apnic/stats/apnic/delegated-apnic-latest -O $FILE\r\nfunction compute_mask()\r\n{\r\nlocal array_ip=($(awk -F \"|\" \'{print $1}\' $1))\r\nlocal array_mask=($(awk -F \"|\" \'{print $2}\' $1))\r\nfor ((i=0,j=0;i&lt;${#array_ip[@]},j&lt;${#array_mask[@]};i++,j++));\r\ndo\r\nev_ip=${array_ip[$i]}\r\nev_mask=${array_mask[j]}\r\nfor ((a=1;a&lt;=32;a++));\r\ndo\r\nif [ `echo $[2**$a]` -eq $ev_mask ];then\r\nar_mask=`expr 32 - $a`\r\necho \"$ev_ip/$ar_mask\"\r\nfi\r\ndone\r\ndone\r\n}\r\n#China routing table\r\ngrep \"CN|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/cnip.txt\r\ncompute_mask $DIR/cnip.txt &gt;&gt;cnip.txt\r\n#Hong Kong routing table\r\ngrep \"HK|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/hkip.txt\r\ncompute_mask $DIR/hkip.txt &gt;&gt;hkip.txt\r\n#Australia\'s routing table\r\ngrep \"AU|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/auip.txt\r\ncompute_mask $DIR/auip.txt &gt;&gt;auip.txt\r\n#South Korea\'s routing table\r\ngrep \"KR|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/krip.txt\r\ncompute_mask $DIR/krip.txt &gt;&gt;krip.txt\r\n#Tai Wan routing table\r\ngrep \"TW|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/twip.txt\r\ncompute_mask $DIR/twip.txt &gt;&gt;twip.txt\r\n#Singapore routing table\r\ngrep \"SG|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/sgip.txt\r\ncompute_mask $DIR/sgip.txt &gt;&gt;sgip.txt\r\n#Japan\'s routing table\r\ngrep \"JP|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/jpip.txt\r\ncompute_mask $DIR/jpip.txt &gt;&gt;jpip.txt', '下载IP地址表', '', 'inherit', 'closed', 'closed', '', '55-revision-v1', '', '', '2017-10-30 23:20:11', '2017-10-30 15:20:11', '', '55', 'https://blog.csking.cn/?p=57', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('59', '1', '2017-10-30 23:21:47', '2017-10-30 15:21:47', '#!/bin/bash\r\nDate=`date +%Y%m%d%H%M`\r\nFILE=/home/apnic.txt\r\nmkdir /home/ip$Date\r\nDIR=\"/home/ip$Date\"\r\nrm -f $FILE\r\nwget http://ftp.apnic.net/apnic/stats/apnic/delegated-apnic-latest -O $FILE\r\nfunction compute_mask()\r\n{\r\nlocal array_ip=($(awk -F \"|\" \'{print $1}\' $1))\r\nlocal array_mask=($(awk -F \"|\" \'{print $2}\' $1))\r\nfor ((i=0,j=0;i&lt;${#array_ip[@]},j&lt;${#array_mask[@]};i++,j++));\r\ndo\r\nev_ip=${array_ip[$i]}\r\nev_mask=${array_mask[j]}\r\nfor ((a=1;a&lt;=32;a++));\r\ndo\r\nif [ `echo $[2**$a]` -eq $ev_mask ];then\r\nar_mask=`expr 32 - $a`\r\necho \"$ev_ip/$ar_mask\"\r\nfi\r\ndone\r\ndone\r\n}\r\n#China routing table\r\ngrep \"CN|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/cnip.txt\r\ncompute_mask $DIR/cnip.txt &gt;&gt;cnip.txt\r\n#Hong Kong routing table\r\ngrep \"HK|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/hkip.txt\r\ncompute_mask $DIR/hkip.txt &gt;&gt;hkip.txt\r\n#Australia\'s routing table\r\ngrep \"AU|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/auip.txt\r\ncompute_mask $DIR/auip.txt &gt;&gt;auip.txt\r\n#South Korea\'s routing table\r\ngrep \"KR|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/krip.txt\r\ncompute_mask $DIR/krip.txt &gt;&gt;krip.txt\r\n#Tai Wan routing table\r\ngrep \"TW|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/twip.txt\r\ncompute_mask $DIR/twip.txt &gt;&gt;twip.txt\r\n#Singapore routing table\r\ngrep \"SG|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/sgip.txt\r\ncompute_mask $DIR/sgip.txt &gt;&gt;sgip.txt\r\n#Japan\'s routing table\r\ngrep \"JP|ipv4\" /home/apnic.txt |awk -F \"|\" \'{print $4\"|\"$5}\' &gt;$DIR/jpip.txt\r\ncompute_mask $DIR/jpip.txt &gt;&gt;jpip.txt', '通过shell在APNIC上获取IP地址路由表', '', 'inherit', 'closed', 'closed', '', '55-revision-v1', '', '', '2017-10-30 23:21:47', '2017-10-30 15:21:47', '', '55', 'https://blog.csking.cn/?p=59', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('67', '1', '2017-10-30 23:32:38', '2017-10-30 15:32:38', '以Centos7 为例：\r\n\r\n<strong>一、安装mtr软件</strong>\r\n<pre lang=\"bash\" line=\"1\">\r\nyum install -y mtr\r\n</pre>\r\n<strong>二、在home目录下新建一个文件mtr.sh，然后往文件中输入以下内容：</strong>\r\n<pre lang=\"bash\" line=\"1\">\r\n#!/bin/bash\r\nip=(\r\n220.181.22.1 61.152.81.1 202.97.26.54 202.97.70.1 61.136.171.1 202.97.27.1 60.30.128.1 210.22.67.1 120.84.0.1 221.10.251.1 218.104.110.1 58.240.48.43\r\n120.197.84.65\r\n218.205.128.1\r\n120.193.0.1\r\n211.162.78.1\r\n211.161.159.1\r\n211.161.192.1\r\n)\r\nfor ((i=0;i<${#ip[@]};i++));do\r\n{\r\nmtr -r -c 30 -n ${ip[$i]}|sed \'1d\'|awk \'BEGIN{print \"----------------------\'\"${ip[$i]}\"\'------------------------\"strftime(\"%Y/%m/%d %H:%M:%S\")}{print $0}END{print \"\\n\"}\' >>/home/${ip[$i]}.txt &\r\n}\r\ndone#!/bin/bash\r\nip=(\r\n120.197.84.65\r\n218.205.128.1\r\n120.193.0.1\r\n211.162.78.1\r\n211.161.159.1\r\n211.161.192.1\r\n)\r\nfor ((i=0;i<${#ip[@]};i++));do\r\n{\r\nmtr -r -c 30 -n ${ip[$i]}|sed \'1d\'|awk \'BEGIN{print \"----------------------\'\"${ip[$i]}\"\'------------------------\"strftime(\"%Y/%m/%d %H:%M:%S\")}{print $0}END{print \"\\n\"}\' >>/home/${ip[$i]}.txt &\r\n}\r\ndone\r\n</pre>\r\n<strong>三、给mtr文件设置可执行权限</strong>：\r\n<pre lang=\"bash\" line=\"1\">\r\nchmod 755 mtr.sh\r\n</pre>\r\n&nbsp;\r\n\r\n<strong>四、将文件加入crontab中，让该文件每分钟执行一次</strong>\r\n\r\n1、执行：<pre lang=\"bash\" line=\"1\">crontab  -e</pre>\r\n\r\n2、输入：<pre lang=\"bash\" line=\"1\">*/1 * * * * /home/mtr.sh</pre>', '通过mtr脚本，定位网络故障点', '', 'publish', 'open', 'open', '', '%e9%80%9a%e8%bf%87mtr%e8%84%9a%e6%9c%ac%ef%bc%8c%e5%ae%9a%e4%bd%8d%e7%bd%91%e7%bb%9c%e6%95%85%e9%9a%9c%e7%82%b9', '', '', '2017-10-31 02:02:56', '2017-10-30 18:02:56', '', '0', 'https://blog.csking.cn/?p=67', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('69', '1', '2017-10-30 23:31:51', '2017-10-30 15:31:51', '以Centos7 为例：\r\n\r\n一、安装mtr软件\r\n\r\nyum install -y mtr\r\n\r\n二、在home目录下新建一个文件mtr.sh，然后往文件中输入以下内容：\r\n\r\n#!/bin/bash\r\nip=(\r\n120.197.84.65\r\n218.205.128.1\r\n120.193.0.1\r\n211.162.78.1\r\n211.161.159.1\r\n211.161.192.1\r\n)\r\nfor ((i=0;i&lt;${#ip[@]};i++));do\r\n{\r\nmtr -r -c 30 -n ${ip[$i]}|sed \'1d\'|awk \'BEGIN{print \"----------------------\'\"${ip[$i]}\"\'------------------------\"strftime(\"%Y/%m/%d %H:%M:%S\")}{print $0}END{print \"\\n\"}\' &gt;&gt;/home/${ip[$i]}.txt &amp;\r\n}\r\ndone\r\n\r\n三、给mtr文件设置可执行权限：\r\n\r\nchmod 755 mtr.sh\r\n\r\n&nbsp;\r\n\r\n四、将文件加入crontab中，让该文件每分钟执行一次\r\n\r\n1、执行：crontab  -e\r\n\r\n2、输入：*/1 * * * * /home/mtr.sh', '通过mtr脚本，定位网络故障点', '', 'inherit', 'closed', 'closed', '', '67-revision-v1', '', '', '2017-10-30 23:31:51', '2017-10-30 15:31:51', '', '67', 'https://blog.csking.cn/?p=69', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('71', '1', '2017-10-30 23:32:38', '2017-10-30 15:32:38', '以Centos7 为例：\r\n\r\n<strong>一、安装mtr软件</strong>\r\n\r\nyum install -y mtr\r\n\r\n<strong>二、在home目录下新建一个文件mtr.sh，然后往文件中输入以下内容：</strong>\r\n\r\n#!/bin/bash\r\nip=(\r\n120.197.84.65\r\n218.205.128.1\r\n120.193.0.1\r\n211.162.78.1\r\n211.161.159.1\r\n211.161.192.1\r\n)\r\nfor ((i=0;i&lt;${#ip[@]};i++));do\r\n{\r\nmtr -r -c 30 -n ${ip[$i]}|sed \'1d\'|awk \'BEGIN{print \"----------------------\'\"${ip[$i]}\"\'------------------------\"strftime(\"%Y/%m/%d %H:%M:%S\")}{print $0}END{print \"\\n\"}\' &gt;&gt;/home/${ip[$i]}.txt &amp;\r\n}\r\ndone\r\n\r\n<strong>三、给mtr文件设置可执行权限</strong>：\r\n\r\nchmod 755 mtr.sh\r\n\r\n&nbsp;\r\n\r\n<strong>四、将文件加入crontab中，让该文件每分钟执行一次</strong>\r\n\r\n1、执行：crontab  -e\r\n\r\n2、输入：*/1 * * * * /home/mtr.sh', '通过mtr脚本，定位网络故障点', '', 'inherit', 'closed', 'closed', '', '67-revision-v1', '', '', '2017-10-30 23:32:38', '2017-10-30 15:32:38', '', '67', 'https://blog.csking.cn/?p=71', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('73', '1', '2017-10-30 23:35:52', '2017-10-30 15:35:52', '<pre lang=\"python\" line=\"1\">\r\n#!/usr/bin/env python3.4\r\n#coding:utf8\r\n\r\nimport sys, os, getpass\r\n\r\nos.system(\'clear\')\r\n\r\nreg_count = 0\r\n#user register\r\nwhile (reg_count < 3):\r\n    reg_name = input(\"Please input user name: \") \r\n    if(reg_name == \"\" ):continue\r\n\r\n    login_acc = open(r\"account.txt\", \"r\")\r\n    read_info = login_acc.read()\r\n    list = read_info.split()\r\n    if ( reg_name == list[0]):\r\n        print(\"The user already exists.\")\r\n        login_acc.close()\r\n        break\r\n\r\n    reg_pass = input(\"Please input your password: \")\r\n    if(reg_pass == \"\"):continue\r\n\r\n    confirm_pass = input(\"Please enter password again: \")\r\n\r\n\r\n    if confirm_pass == reg_pass:\r\n        reg_acc = open(\"account.txt\", \"w+\")\r\n        reg_acc.write( str(reg_name) + \' \' + str(reg_pass) + \"\\n\" )\r\n        reg_acc.close()\r\n        print (\"You have redistered successfully.\")\r\n        break\r\n\r\n    else:\r\n        print (\"Entered passwords differ.\")\r\n        reg_count = reg_count + 1\r\n\r\n#print (\"Your username is \"+ list[0]+\"\\n\"+\"Your password is \" + str(reg_pass))\r\n\r\n#user login\r\nlogin_acc = open(r\"account.txt\", \"r\")\r\nread_info = login_acc.read()\r\n\r\nlist = read_info.split()\r\n\r\nlogin_count = 0\r\nwhile (login_count < 3):\r\n    login_name = input(\"Login Name: \")\r\n    login_pass = input(\"Login passwd:\")\r\n\r\n    lock_file = open(r\"lockuser.txt\",\'r\')\r\n    lock_info = lock_file.read()\r\n    lock_list = lock_info.split()\r\n    print (lock_list)\r\n    if login_name == lock_list[0]:\r\n        print (\"Account locked.\")\r\n        sys.exit(0)\r\n\r\n    elif login_name != list[0] or login_pass != list[1]:\r\n        login_count = login_count + 1\r\n\r\n        if login_name != list[0]:\r\n            print (\"Username not exist, The number of times:\", 3 - login_count)\r\n\r\n        elif  login_pass != list[1]:\r\n            print (\"Username and password not match, The number of times:\", 3 - login_count) \r\n\r\n            if ( int(login_count) == 3):\r\n                lock_file = open(r\"lockuser.txt\",\'w+\')\r\n                lock_file.write( str(login_name)+\"\\n\")\r\n                lock_file.close()\r\n                 \r\n                print(\"Account locked.\")\r\n            else:\r\n                continue\r\n\r\n#    elif login_name == list[0] and login_pass == list[1]:\r\n    else:\r\n        print (\"Welcome to our System.\")\r\n        break\r\n\r\nlogin_acc.close()\r\n</pre>', 'Python登陆脚本', '', 'publish', 'open', 'open', '', 'python%e7%99%bb%e9%99%86%e8%84%9a%e6%9c%ac', '', '', '2017-10-31 02:02:23', '2017-10-30 18:02:23', '', '0', 'https://blog.csking.cn/?p=73', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('75', '1', '2017-10-30 23:35:52', '2017-10-30 15:35:52', '#!/usr/bin/env python3.4\r\n#coding:utf8\r\n\r\nimport sys, os, getpass\r\n\r\nos.system(\'clear\')\r\n\r\nreg_count = 0\r\n#user register\r\nwhile (reg_count &lt; 3):\r\nreg_name = input(\"Please input user name: \")\r\nif(reg_name == \"\" ):continue\r\n\r\nlogin_acc = open(r\"account.txt\", \"r\")\r\nread_info = login_acc.read()\r\nlist = read_info.split()\r\nif ( reg_name == list[0]):\r\nprint(\"The user already exists.\")\r\nlogin_acc.close()\r\nbreak\r\n\r\nreg_pass = input(\"Please input your password: \")\r\nif(reg_pass == \"\"):continue\r\n\r\nconfirm_pass = input(\"Please enter password again: \")\r\n\r\n&nbsp;\r\n\r\nif confirm_pass == reg_pass:\r\nreg_acc = open(\"account.txt\", \"w+\")\r\nreg_acc.write( str(reg_name) + \' \' + str(reg_pass) + \"\\n\" )\r\nreg_acc.close()\r\nprint (\"You have redistered successfully.\")\r\nbreak\r\n\r\nelse:\r\nprint (\"Entered passwords differ.\")\r\nreg_count = reg_count + 1\r\n\r\n#print (\"Your username is \"+ list[0]+\"\\n\"+\"Your password is \" + str(reg_pass))\r\n\r\n#user login\r\nlogin_acc = open(r\"account.txt\", \"r\")\r\nread_info = login_acc.read()\r\n\r\nlist = read_info.split()\r\n\r\nlogin_count = 0\r\nwhile (login_count &lt; 3):\r\nlogin_name = input(\"Login Name: \")\r\nlogin_pass = input(\"Login passwd:\")\r\n\r\nlock_file = open(r\"lockuser.txt\",\'r\')\r\nlock_info = lock_file.read()\r\nlock_list = lock_info.split()\r\nprint (lock_list)\r\nif login_name == lock_list[0]:\r\nprint (\"Account locked.\")\r\nsys.exit(0)\r\n\r\nelif login_name != list[0] or login_pass != list[1]:\r\nlogin_count = login_count + 1\r\n\r\nif login_name != list[0]:\r\nprint (\"Username not exist, The number of times:\", 3 - login_count)\r\n\r\nelif login_pass != list[1]:\r\nprint (\"Username and password not match, The number of times:\", 3 - login_count)\r\n\r\nif ( int(login_count) == 3):\r\nlock_file = open(r\"lockuser.txt\",\'w+\')\r\nlock_file.write( str(login_name)+\"\\n\")\r\nlock_file.close()\r\n\r\nprint(\"Account locked.\")\r\nelse:\r\ncontinue\r\n\r\n# elif login_name == list[0] and login_pass == list[1]:\r\nelse:\r\nprint (\"Welcome to our System.\")\r\nbreak\r\n\r\nlogin_acc.close()', 'Python登陆脚本', '', 'inherit', 'closed', 'closed', '', '73-revision-v1', '', '', '2017-10-30 23:35:52', '2017-10-30 15:35:52', '', '73', 'https://blog.csking.cn/?p=75', '0', 'revision', '', '0');
