INSERT INTO `wp_posts` VALUES ('255', '1', '2017-10-31 10:31:21', '2017-10-31 02:31:21', '<pre lang=\"bash\">\r\ncat /etc/redhat-release |awk \'{print$1}\'\r\n\r\nlsb_release -a |awk -F \":\" \'NR==2{print$2}\'|awk \'{sub(/^[[:blank:]]*/,\"\",$0);print$0}\'\r\n</pre>', 'Centos7输出系统名称', '', 'publish', 'open', 'open', '', 'centos7%e8%be%93%e5%87%ba%e7%b3%bb%e7%bb%9f%e5%90%8d%e7%a7%b0', '', '', '2017-10-31 10:31:21', '2017-10-31 02:31:21', '', '0', 'https://blog.csking.cn/?p=255', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('257', '1', '2017-10-31 10:31:21', '2017-10-31 02:31:21', '<pre lang=\"bash\">\r\ncat /etc/redhat-release |awk \'{print$1}\'\r\n\r\nlsb_release -a |awk -F \":\" \'NR==2{print$2}\'|awk \'{sub(/^[[:blank:]]*/,\"\",$0);print$0}\'\r\n</pre>', 'Centos7输出系统名称', '', 'inherit', 'closed', 'closed', '', '255-revision-v1', '', '', '2017-10-31 10:31:21', '2017-10-31 02:31:21', '', '255', 'https://blog.csking.cn/?p=257', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('259', '1', '2017-10-31 10:34:46', '2017-10-31 02:34:46', '1.查看磁盘名称\r\n<pre lang=\"bash\">\r\nfdisk -l \r\n</pre>\r\n2.f分区\r\n<pre lang=\"bash\">\r\nfdisk /dev/vdb \r\n</pre>\r\n依次输入n--p--1--回车--回车--wq\r\n3.格式化硬盘\r\n<pre lang=\"bash\">\r\nmkfs.ext4 /dev/vdb1  \r\n</pre>\r\n4.将磁盘挂载到data目录\r\n<pre lang=\"bash\">\r\nmkdir /data\r\nmount /dev/vdb1 /data \r\necho \"UUID=`ls -l /dev/disk/by-uuid/ |grep vdb |awk  \'{print$9}\'|grep -v ^$` /data     ext4       defaults    0 0 \" >> /etc/fstab\r\n</pre>\r\nUUID查看方法 \r\n<pre lang=\"bash\">\r\nls -l /dev/disk/by-uuid\r\n</pre>', '阿里云服务器磁盘挂载', '', 'publish', 'open', 'open', '', '%e9%98%bf%e9%87%8c%e4%ba%91%e6%9c%8d%e5%8a%a1%e5%99%a8%e7%a3%81%e7%9b%98%e6%8c%82%e8%bd%bd', '', '', '2017-10-31 10:34:46', '2017-10-31 02:34:46', '', '0', 'https://blog.csking.cn/?p=259', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('261', '1', '2017-10-31 10:34:46', '2017-10-31 02:34:46', '1.查看磁盘名称\r\n<pre lang=\"bash\">\r\nfdisk -l \r\n</pre>\r\n2.f分区\r\n<pre lang=\"bash\">\r\nfdisk /dev/vdb \r\n</pre>\r\n依次输入n--p--1--回车--回车--wq\r\n3.格式化硬盘\r\n<pre lang=\"bash\">\r\nmkfs.ext4 /dev/vdb1  \r\n</pre>\r\n4.将磁盘挂载到data目录\r\n<pre lang=\"bash\">\r\nmkdir /data\r\nmount /dev/vdb1 /data \r\necho \"UUID=`ls -l /dev/disk/by-uuid/ |grep vdb |awk  \'{print$9}\'|grep -v ^$` /data     ext4       defaults    0 0 \" >> /etc/fstab\r\n</pre>\r\nUUID查看方法 \r\n<pre lang=\"bash\">\r\nls -l /dev/disk/by-uuid\r\n</pre>', '阿里云服务器磁盘挂载', '', 'inherit', 'closed', 'closed', '', '259-revision-v1', '', '', '2017-10-31 10:34:46', '2017-10-31 02:34:46', '', '259', 'https://blog.csking.cn/?p=261', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('263', '1', '2017-10-31 10:43:44', '2017-10-31 02:43:44', '1.建立一个文件空间\r\n<pre lang=\"bash\">\r\ndd if=/dev/zero of=/home/.swap bs=1M count=1024\r\n</pre>\r\n2.格式化为swap分区\r\n<pre lang=\"bash\">\r\nmkswap /home/.swap\r\n</pre>\r\n3.设置文件权限\r\n<pre lang=\"bash\">\r\nchmod 600 /home/.swap\r\n</pre>\r\n4.开启swap分区\r\n<pre lang=\"bash\">\r\nswapon /home/.swap\r\n</pre>\r\n5.将swap分区加入开机启动中\r\n<pre lang=\"bash\">\r\nvi /etc/fstab\r\nUUID=\"\"    swap  swap  defaults 0 0\r\n</pre>\r\n6.查看是否挂载成功\r\n<pre lang=\"bash\">\r\nfree -m\r\n</pre>', 'SWAP交换空间设置', '', 'publish', 'open', 'open', '', 'swap%e4%ba%a4%e6%8d%a2%e7%a9%ba%e9%97%b4%e8%ae%be%e7%bd%ae', '', '', '2017-10-31 10:43:44', '2017-10-31 02:43:44', '', '0', 'https://blog.csking.cn/?p=263', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('265', '1', '2017-10-31 10:43:44', '2017-10-31 02:43:44', '1.建立一个文件空间\r\n<pre lang=\"bash\">\r\ndd if=/dev/zero of=/home/.swap bs=1M count=1024\r\n</pre>\r\n2.格式化为swap分区\r\n<pre lang=\"bash\">\r\nmkswap /home/.swap\r\n</pre>\r\n3.设置文件权限\r\n<pre lang=\"bash\">\r\nchmod 600 /home/.swap\r\n</pre>\r\n4.开启swap分区\r\n<pre lang=\"bash\">\r\nswapon /home/.swap\r\n</pre>\r\n5.将swap分区加入开机启动中\r\n<pre lang=\"bash\">\r\nvi /etc/fstab\r\nUUID=\"\"    swap  swap  defaults 0 0\r\n</pre>\r\n6.查看是否挂载成功\r\n<pre lang=\"bash\">\r\nfree -m\r\n</pre>', 'SWAP交换空间设置', '', 'inherit', 'closed', 'closed', '', '263-revision-v1', '', '', '2017-10-31 10:43:44', '2017-10-31 02:43:44', '', '263', 'https://blog.csking.cn/?p=265', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('267', '1', '2017-10-31 11:32:15', '2017-10-31 03:32:15', '<pre lang=\"bash\">\r\n#/bin/bash\r\nfor i in `cat http.txt`;do\r\n  dig @114.114.114.114 $i |awk \'/cn|com/\'|awk  \'{print$5}\' |grep 1 >> http_filter.txt\r\n  dig @8.8.8.8 $i |awk \'/cn|com/\'|awk  \'{print$5}\' |grep 1 >> http_filter.txt\r\ndone\r\n</pre>', '批量解析域名IP', '', 'publish', 'open', 'open', '', '%e6%89%b9%e9%87%8f%e8%a7%a3%e6%9e%90%e5%9f%9f%e5%90%8dip', '', '', '2017-10-31 11:32:15', '2017-10-31 03:32:15', '', '0', 'https://blog.csking.cn/?p=267', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('269', '1', '2017-10-31 11:32:15', '2017-10-31 03:32:15', '<pre lang=\"bash\">\r\n#/bin/bash\r\nfor i in `cat http.txt`;do\r\n  dig @114.114.114.114 $i |awk \'/cn|com/\'|awk  \'{print$5}\' |grep 1 >> http_filter.txt\r\n  dig @8.8.8.8 $i |awk \'/cn|com/\'|awk  \'{print$5}\' |grep 1 >> http_filter.txt\r\ndone\r\n</pre>', '批量解析域名IP', '', 'inherit', 'closed', 'closed', '', '267-revision-v1', '', '', '2017-10-31 11:32:15', '2017-10-31 03:32:15', '', '267', 'https://blog.csking.cn/?p=269', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('271', '1', '2017-10-31 11:34:42', '2017-10-31 03:34:42', '<pre lang=\"bash\">\r\n#/bin/bash\r\n#\r\n#wang_chongsheng@163.com\r\n##2017-10-30\r\n##JDK&&MySQL下载\r\nDpath=\'/opt/install/src\'\r\n\r\necho 应用编号：\r\necho 1.jdk-8u152-linux-x64.tar.gz\r\necho 2.mysql-5.7.17-linux-glibc2.5-x86_64.tar.gz\r\necho 3.jdk and MySQL\r\necho \r\nread -p \"input a val:\" val\r\nif [ $val == 1 ]; then\r\n    wget -P $Dpath --no-check-certificate --no-cookie --header \"Cookie: oraclelicense=accept-securebackup-cookie;\" http://download.oracle.com/otn-pub/java/jdk/8u152-b16/aa0333dd3019491ca4f6ddbe78cdb6d0/jdk-8u152-linux-x64.tar.gz\r\nelif [ $val == 2 ]; then\r\n    wget -P $Dpath http://mirrors.sohu.com/mysql/MySQL-5.7/mysql-5.7.17-linux-glibc2.5-x86_64.tar.gz\r\nelif [ $val == 3 ]; then\r\n    wget -P $Dpath http://mirrors.sohu.com/mysql/MySQL-5.7/mysql-5.7.17-linux-glibc2.5-x86_64.tar.gz\r\n    wget -P $Dpath --no-check-certificate --no-cookie --header \"Cookie: oraclelicense=accept-securebackup-cookie;\" http://download.oracle.com/otn-pub/java/jdk/8u152-b16/aa0333dd3019491ca4f6ddbe78cdb6d0/jdk-8u152-linux-x64.tar.gz\r\nelse\r\n    echo \'INVALID ENTRY\'\r\nfi\r\n</pre>', 'JDK8以及MySQL二进制包下载', '', 'publish', 'open', 'open', '', 'jdk8%e4%bb%a5%e5%8f%8amysql%e4%ba%8c%e8%bf%9b%e5%88%b6%e5%8c%85%e4%b8%8b%e8%bd%bd', '', '', '2017-10-31 11:35:12', '2017-10-31 03:35:12', '', '0', 'https://blog.csking.cn/?p=271', '0', 'post', '', '0');
