INSERT INTO `wp_posts` VALUES ('275', '1', '2017-10-31 11:35:12', '2017-10-31 03:35:12', '<pre lang=\"bash\">\r\n#/bin/bash\r\n#\r\n#wang_chongsheng@163.com\r\n##2017-10-30\r\n##JDK&&MySQL下载\r\nDpath=\'/opt/install/src\'\r\n\r\necho 应用编号：\r\necho 1.jdk-8u152-linux-x64.tar.gz\r\necho 2.mysql-5.7.17-linux-glibc2.5-x86_64.tar.gz\r\necho 3.jdk and MySQL\r\necho \r\nread -p \"input a val:\" val\r\nif [ $val == 1 ]; then\r\n    wget -P $Dpath --no-check-certificate --no-cookie --header \"Cookie: oraclelicense=accept-securebackup-cookie;\" http://download.oracle.com/otn-pub/java/jdk/8u152-b16/aa0333dd3019491ca4f6ddbe78cdb6d0/jdk-8u152-linux-x64.tar.gz\r\nelif [ $val == 2 ]; then\r\n    wget -P $Dpath http://mirrors.sohu.com/mysql/MySQL-5.7/mysql-5.7.17-linux-glibc2.5-x86_64.tar.gz\r\nelif [ $val == 3 ]; then\r\n    wget -P $Dpath http://mirrors.sohu.com/mysql/MySQL-5.7/mysql-5.7.17-linux-glibc2.5-x86_64.tar.gz\r\n    wget -P $Dpath --no-check-certificate --no-cookie --header \"Cookie: oraclelicense=accept-securebackup-cookie;\" http://download.oracle.com/otn-pub/java/jdk/8u152-b16/aa0333dd3019491ca4f6ddbe78cdb6d0/jdk-8u152-linux-x64.tar.gz\r\nelse\r\n    echo \'INVALID ENTRY\'\r\nfi\r\n</pre>', 'JDK8以及MySQL二进制包下载', '', 'inherit', 'closed', 'closed', '', '271-revision-v1', '', '', '2017-10-31 11:35:12', '2017-10-31 03:35:12', '', '271', 'https://blog.csking.cn/?p=275', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('277', '1', '2017-10-31 11:39:10', '2017-10-31 03:39:10', '通过脚本中的find查找需要备份或更新的路径，执行文件备份\r\n<pre lang=\"bash\">\r\n#!/bin/bash\r\n#uset -x\r\n\r\n#wang_chongsheng@163.com\r\n\r\n#2017-08-25\r\n\r\nbackup_dir=${B_DIR}\r\nupdate_dir=\"\"\r\ndate=\"$(date  +\"%Y%m%d%H\")\"\r\n\r\nwhile getopts \'b:u:\' opt ; do\r\n        case $opt in\r\n        b)\r\n        backup_dir=\"$OPTARG\";;\r\n        u)\r\n        update_dir=\"$OPTARG\";;\r\n        *)\r\n        echo -e \"Useage: $0 -b backup_dir -u update_dir\" ;;\r\nesac\r\ndone\r\n\r\n#[ \"$backup_dir\" == \"\" ]&&{ echo \"no backup_dir\" ; exit 1 ; }\r\n[ \"$update_dir\" == \"\" ]&&{ echo \"no update_dir\" ; exit 2 ; }\r\n\r\nfind_dir=\"$(find /home -name $update_dir -type d)\"\r\nparent_dir=\"$(dirname $find_dir)\"\r\nfind_tar=\"$(find /home -name ${update_dir}.tar.gz)\"\r\nfind_zip=\"$(find /home -name ${update_dir}.zip)\"\r\n\r\n#echo cd $find_dir\r\n#backup file\r\ncd $parent_dir\r\n#mv ${backup_dir} ${backup_dir}-${date}\r\ntar cf ${update_dir}-${date}.tar.gz ${update_dir}\r\n\r\necho -e \"Note: \\033[33mOld file backup to $parent_dir/${update_dir}-${date}.tar.gz \\033[0m\" \r\n\r\nsleep 1\r\n#delete file\r\n#rm -rf $find_dir\r\n\r\n#update file\r\nif [ ! -f \"$find_tar\" ] && [ -f \"$find_zip\" ];then\r\n        rm -rf $find_dir\r\n        unzip $find_zip -C  $parent_dir\r\n        echo -e \"\\033[31m Note: File update done. \\033[0m\" \r\nelif [ ! -f \"$find_zip\" ] && [ -f \"$find_tar\" ];then\r\n        rm -rf $find_dir\r\n        tar xf  $find_tar  -C  $parent_dir\r\n        echo -e \"\\033[31m Note:  File update done. \\033[0m\"\r\nelif [ -f \"$find_tar\" ] && [ -f \"$find_zip\" ];then\r\n        echo \"Error: Update failed. Please check ${update_dir}.tar.gz or ${update_dir}.zip is used.\"\r\nelif [ ! -f \"$find_zip\" ] && [ ! -f \"$find_tar\" ];then\r\n        echo \"Error: Update failed. ${update_dir} file not found, Please check if the file exists.\"\r\nfi\r\n</pre>', '文件的更新和备份', '', 'publish', 'open', 'open', '', '%e4%b8%8a%e4%bc%a0%e6%96%87%e4%bb%b6%e5%88%b0%e6%8c%87%e5%ae%9a%e6%96%87%e4%bb%b6%e5%a4%b9', '', '', '2017-10-31 11:51:42', '2017-10-31 03:51:42', '', '0', 'https://blog.csking.cn/?p=277', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('279', '1', '2017-10-31 11:39:10', '2017-10-31 03:39:10', '指定需要备份的文件夹以及需要更新的文件路径\r\n<pre lang=\"bash\">\r\n#!/bin/bash\r\n#uset -x\r\n\r\n#wang_chongsheng@163.com\r\n\r\n#2017-08-25\r\n\r\nbackup_dir=${B_DIR}\r\nupdate_dir=\"\"\r\ndate=\"$(date  +\"%Y%m%d%H\")\"\r\n\r\nwhile getopts \'b:u:\' opt ; do\r\n        case $opt in\r\n        b)\r\n        backup_dir=\"$OPTARG\";;\r\n        u)\r\n        update_dir=\"$OPTARG\";;\r\n        *)\r\n        echo -e \"Useage: $0 -b backup_dir -u update_dir\" ;;\r\nesac\r\ndone\r\n\r\n#[ \"$backup_dir\" == \"\" ]&&{ echo \"no backup_dir\" ; exit 1 ; }\r\n[ \"$update_dir\" == \"\" ]&&{ echo \"no update_dir\" ; exit 2 ; }\r\n\r\nfind_dir=\"$(find /home -name $update_dir -type d)\"\r\nparent_dir=\"$(dirname $find_dir)\"\r\nfind_tar=\"$(find /home -name ${update_dir}.tar.gz)\"\r\nfind_zip=\"$(find /home -name ${update_dir}.zip)\"\r\n\r\n#echo cd $find_dir\r\n#backup file\r\ncd $parent_dir\r\n#mv ${backup_dir} ${backup_dir}-${date}\r\ntar cf ${update_dir}-${date}.tar.gz ${update_dir}\r\n\r\necho -e \"Note: \\033[33mOld file backup to $parent_dir/${update_dir}-${date}.tar.gz \\033[0m\" \r\n\r\nsleep 1\r\n#delete file\r\n#rm -rf $find_dir\r\n\r\n#update file\r\nif [ ! -f \"$find_tar\" ] && [ -f \"$find_zip\" ];then\r\n        rm -rf $find_dir\r\n        unzip $find_zip -C  $parent_dir\r\n        echo -e \"\\033[31m Note: File update done. \\033[0m\" \r\nelif [ ! -f \"$find_zip\" ] && [ -f \"$find_tar\" ];then\r\n        rm -rf $find_dir\r\n        tar xf  $find_tar  -C  $parent_dir\r\n        echo -e \"\\033[31m Note:  File update done. \\033[0m\"\r\nelif [ -f \"$find_tar\" ] && [ -f \"$find_zip\" ];then\r\n        echo \"Error: Update failed. Please check ${update_dir}.tar.gz or ${update_dir}.zip is used.\"\r\nelif [ ! -f \"$find_zip\" ] && [ ! -f \"$find_tar\" ];then\r\n        echo \"Error: Update failed. ${update_dir} file not found, Please check if the file exists.\"\r\nfi\r\n</pre>', '上传文件到指定文件夹', '', 'inherit', 'closed', 'closed', '', '277-revision-v1', '', '', '2017-10-31 11:39:10', '2017-10-31 03:39:10', '', '277', 'https://blog.csking.cn/?p=279', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('281', '1', '2017-10-31 11:41:46', '2017-10-31 03:41:46', '通过远程链接到服务器，将备份文件的脚本传送过去，然后更新文件\r\n<pre lang=\"bash\">\r\n#!/bin/bash\r\n#set -x\r\n#wang_chongsheng@163.com 2017-08-25\r\n\r\ndir=${DIR}\r\nhost=\"\"\r\nport=32837\r\nuser=\"root\"\r\ndate=$(date +%Y%m%d-%H)\r\n\r\nwhile getopts \'d:h:p:u:\' opt ; do\r\n        case $opt in\r\n        d)\r\n        dir=\"$OPTARG\";;\r\n        h)\r\n        host=\"$OPTARG\";;\r\n        u)\r\n        user=\"$OPTARG\";;\r\n        p)\r\n        port=\"$OPTARG\";;\r\n        *)\r\n        echo -e \"Useage: $0  -d directory -h host -u user[default root] -p port[default 32837].\" ;;\r\nesac\r\ndone\r\n[ \"$dir\"  == \"\" ]&&{ echo \"Please enter directory name.\" ; exit 1 ; }\r\n[ \"$host\" == \"\" ]&&{ echo \"Please enter host ip.\" ; exit 2 ; }\r\n#[ \"$user\" == \"\" ]&&{ echo \"Please enter username. \" ; exit 3 ; }\r\n#[ \"$port\" == \"\" ]&&{ echo \"Please enter port.\" ; exit 4 ;}\r\n\r\n#look-up file\r\nfind_dir=`find /home -name ${dir} -type d`\r\nzip_file=\"${dir}.tar.gz\"\r\nfind_zip=`find /home -name $zip_file`\r\n\r\ncd $(dirname $find_dir)\r\n\r\n#Compressed file\r\nif [ -d \"$find_dir\" ];then\r\n        tar cvf $zip_file $dir\r\nelif [ ! -d \"$find_dir\" ];then\r\n        echo -e \" \\033[1m Error: $dir directory not found. \\033[0m \"\r\n        exit 3\r\nfi\r\n\r\n#Upload file via sftp\r\nsftp -oPort=$port ${user}@${host} <<EOF\r\n-cd /home/src\r\n-put $find_zip\r\n-put /home/src/update_file.sh\r\nquit\r\nEOF\r\n\r\n#update file\r\n#ssh ${user}@${host} -p $port > /dev/null 2>&1 << eeooff\r\n#ssh -t -t ${user}@${host} -p $port << eeooff\r\nssh ${user}@${host} -p $port << eeooff\r\n/bin/bash /home/src/update_file.sh -u $dir\r\nexit\r\neeooff\r\necho -e \"\\033[1m data update done. \\033[0m \"\r\n</pre>', 'SFTP上传文件', '', 'publish', 'open', 'open', '', 'sftp%e4%b8%8a%e4%bc%a0%e6%96%87%e4%bb%b6', '', '', '2017-10-31 11:41:46', '2017-10-31 03:41:46', '', '0', 'https://blog.csking.cn/?p=281', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('283', '1', '2017-10-31 11:41:46', '2017-10-31 03:41:46', '通过远程链接到服务器，将备份文件的脚本传送过去，然后更新文件\r\n<pre lang=\"bash\">\r\n#!/bin/bash\r\n#set -x\r\n#wang_chongsheng@163.com 2017-08-25\r\n\r\ndir=${DIR}\r\nhost=\"\"\r\nport=32837\r\nuser=\"root\"\r\ndate=$(date +%Y%m%d-%H)\r\n\r\nwhile getopts \'d:h:p:u:\' opt ; do\r\n        case $opt in\r\n        d)\r\n        dir=\"$OPTARG\";;\r\n        h)\r\n        host=\"$OPTARG\";;\r\n        u)\r\n        user=\"$OPTARG\";;\r\n        p)\r\n        port=\"$OPTARG\";;\r\n        *)\r\n        echo -e \"Useage: $0  -d directory -h host -u user[default root] -p port[default 32837].\" ;;\r\nesac\r\ndone\r\n[ \"$dir\"  == \"\" ]&&{ echo \"Please enter directory name.\" ; exit 1 ; }\r\n[ \"$host\" == \"\" ]&&{ echo \"Please enter host ip.\" ; exit 2 ; }\r\n#[ \"$user\" == \"\" ]&&{ echo \"Please enter username. \" ; exit 3 ; }\r\n#[ \"$port\" == \"\" ]&&{ echo \"Please enter port.\" ; exit 4 ;}\r\n\r\n#look-up file\r\nfind_dir=`find /home -name ${dir} -type d`\r\nzip_file=\"${dir}.tar.gz\"\r\nfind_zip=`find /home -name $zip_file`\r\n\r\ncd $(dirname $find_dir)\r\n\r\n#Compressed file\r\nif [ -d \"$find_dir\" ];then\r\n        tar cvf $zip_file $dir\r\nelif [ ! -d \"$find_dir\" ];then\r\n        echo -e \" \\033[1m Error: $dir directory not found. \\033[0m \"\r\n        exit 3\r\nfi\r\n\r\n#Upload file via sftp\r\nsftp -oPort=$port ${user}@${host} <<EOF\r\n-cd /home/src\r\n-put $find_zip\r\n-put /home/src/update_file.sh\r\nquit\r\nEOF\r\n\r\n#update file\r\n#ssh ${user}@${host} -p $port > /dev/null 2>&1 << eeooff\r\n#ssh -t -t ${user}@${host} -p $port << eeooff\r\nssh ${user}@${host} -p $port << eeooff\r\n/bin/bash /home/src/update_file.sh -u $dir\r\nexit\r\neeooff\r\necho -e \"\\033[1m data update done. \\033[0m \"\r\n</pre>', 'SFTP上传文件', '', 'inherit', 'closed', 'closed', '', '281-revision-v1', '', '', '2017-10-31 11:41:46', '2017-10-31 03:41:46', '', '281', 'https://blog.csking.cn/?p=283', '0', 'revision', '', '0');
